INSERT INTO animals (name, category, species, breed, age, gender, size, description, image_url) VALUES
('Барсик', 'Домашнее', 'Кот', 'Британская короткошёрстная', 2, 'Мальчик', 'Средний', 'Спокойный и рассудительный кот. Обожает сидеть на коленях и мурлыкать. Приучен к лотку и когтеточке.', 'https://example.com/cats/barsik.jpg'),
('Мурка', 'Домашнее', 'Кот', 'Дворовая', 1, 'Девочка', 'Маленький', 'Игривая и любознательная кошечка. Очень общительная, любит детей. Быстро адаптируется к новому дому.', 'https://example.com/cats/murka.jpg'),
('Васька', 'Домашнее', 'Кот', 'Сибирская', 4, 'Мальчик', 'Большой', 'Пушистый красавец с изумрудными глазами. Хороший охотник, ловит мышей. Нуждается в регулярном вычёсывании.', 'https://example.com/cats/vasya.jpg'),
('Соня', 'Домашнее', 'Кот', 'Персидская', 5, 'Девочка', 'Средний', 'Флегматичная кошка-увалень. Больше всего на свете любит спать на мягком диване. Очень чистоплотная.', 'https://example.com/cats/sonya.jpg'),
('Рыжик', 'Домашнее', 'Кот', 'Мейн-кун', 3, 'Мальчик', 'Большой', 'Величественный кот с кисточками на ушах. Дружелюбен, умён, хорошо ладит с другими животными.', 'https://example.com/cats/ryzhik.jpg'),
('Ночка', 'Домашнее', 'Кот', 'Бомбейская', 2, 'Девочка', 'Средний', 'Элегантная чёрная кошка с блестящей шерстью. Очень преданная, ходит за хозяином хвостиком.', 'https://example.com/cats/nochka.jpg'),
('Граф', 'Домашнее', 'Кот', 'Сфинкс', 1, 'Мальчик', 'Маленький', 'Экзотический бесшёрстный кот. Очень теплолюбив, нуждается в специальном уходе за кожей. Гипоаллергенный.', 'https://example.com/cats/graf.jpg'),
('Луна', 'Домашнее', 'Кот', 'Сиамская', 2, 'Девочка', 'Средний', 'Разговорчивая и умная кошка с ярко-голубыми глазами. Сильно привязывается к одному хозяину.', 'https://example.com/cats/luna.jpg'),

('Шарик', 'Домашнее', 'Собака', 'Лабрадор-ретривер', 3, 'Мальчик', 'Большой', 'Идеальный семейный пёс. Обожает плавать, приносить мячик и играть с детьми. Прошёл базовую дрессировку.', 'https://example.com/dogs/sharik.jpg'),
('Лайма', 'Домашнее', 'Собака', 'Немецкая овчарка', 4, 'Девочка', 'Большой', 'Умная и преданная собака с охранными качествами. Отлично выполняет команды, может быть поводырём.', 'https://example.com/dogs/laima.jpg'),
('Бусинка', 'Домашнее', 'Собака', 'Чихуахуа', 2, 'Девочка', 'Маленький', 'Маленькая, но с большим характером. Очень преданная своей семье, подходит для квартиры.', 'https://example.com/dogs/businka.jpg'),
('Рекс', 'Домашнее', 'Собака', 'Доберман', 5, 'Мальчик', 'Большой', 'Сильная и грациозная собака. Нуждается в активном хозяине и регулярных тренировках. Отличный охранник.', 'https://example.com/dogs/reks.jpg'),
('Боня', 'Домашнее', 'Собака', 'Золотистый ретривер', 1, 'Мальчик', 'Средний', 'Щенок с золотым характером. Очень дружелюбный, любит всех людей и животных. Нуждается в воспитании.', 'https://example.com/dogs/bonya.jpg'),
('Жучка', 'Домашнее', 'Собака', 'Дворняга', 2, 'Девочка', 'Средний', 'Умная и выносливая собака. Подобрана на улице, прошла курс социализации. Очень благодарная и преданная.', 'https://example.com/dogs/zhuchka.jpg'),
('Хатико', 'Домашнее', 'Собака', 'Акита-ину', 6, 'Мальчик', 'Большой', 'Благородная и спокойная собака. Предан как легендарный Хатико. Нуждается в опытном хозяине.', 'https://example.com/dogs/hatiko.jpg'),

('Кеша', 'Домашнее', 'Попугай', 'Волнистый попугай', 3, 'Мальчик', 'Маленький', 'Говорящий попугайчик. Знает 15 слов, любит петь и играть с зеркальцем. Очень общительный.', 'https://example.com/birds/kesha.jpg'),
('Гоша', 'Домашнее', 'Попугай', 'Корелла', 5, 'Мальчик', 'Средний', 'Хохлатый попугай с оранжевыми щёчками. Может насвистывать мелодии, дружелюбный, любит сидеть на плече.', 'https://example.com/birds/gosha.jpg'),

('Тортила', 'Домашнее', 'Черепаха', 'Красноухая черепаха', 8, 'Девочка', 'Средний', 'Спокойная черепаха. Любит греться под лампой и плавать в аквариуме. Ест специальный корм и овощи.', 'https://example.com/reptiles/tortila.jpg'),
('Спотти', 'Домашнее', 'Черепаха', 'Среднеазиатская черепаха', 10, 'Мальчик', 'Средний', 'Наземная черепаха. Медлительный и милый, любит полакомиться одуванчиками. Живёт в террариуме.', 'https://example.com/reptiles/spotty.jpg'),

('Игорь', 'Экзотическое', 'Игуана', 'Зелёная игуана', 2, 'Мальчик', 'Большой', 'Крупная игуана с яркой зелёной окраской. Требует просторного террариума с УФ-лампой, влажностью и бассейном.', 'https://example.com/exotic/igor.jpg'),
('Лори', 'Экзотическое', 'Лемур', 'Кошачий лемур', 4, 'Девочка', 'Средний', 'Очаровательный лемур с полосатым хвостом. Активна в сумерках, питается фруктами, насекомыми. Нужен вольер.', 'https://example.com/exotic/lori.jpg'),
('Зорро', 'Экзотическое', 'Змея', 'Королевский питон', 3, 'Мальчик', 'Средний', 'Спокойный неядовитый питон. Кормится замороженными грызунами раз в 10 дней. Для опытных владельцев.', 'https://example.com/exotic/zorro.jpg')

